function test2(str, index) {
    const result = [];

    while (index < str.length) {
        let endIndex = index + 500 <= str.length ? index + 500 : str.length;
        let sub = str.slice(index, endIndex);
        let last = sub.lastIndexOf(".");

        if (last !== -1) {
            result.push({
                "text": str.slice(index, index + last + 1)
            });
            index += last + 1;
        } else {
            result.push({
                "text": str.slice(index, endIndex)
            });
            index = endIndex;
        }
    }

    return result;
}

